const snowContainer = document.getElementById('snow-container');

// Вставьте сюда свою Base64-картинку
const snowflakeBase64 = 'data:image/png;base64,'; // ваша Base64 строка

function createSnowflake() {
    const snowflake = document.createElement('img');
    snowflake.src = snowflakeBase64;
    snowflake.style.position = 'absolute';
    snowflake.style.left = Math.random() * 100 + 'vw';
    
    const size = Math.random() * 20 + 10; // размер снежинки от 10px до 30px
    snowflake.style.width = size + 'px';
    snowflake.style.height = size + 'px';
    
    // Случайная скорость падения
    const duration = Math.random() * 3 + 2; // 2-5 секунд
    snowflake.style.transition = `transform ${duration}s linear, opacity ${duration}s linear`;
    
    snowContainer.appendChild(snowflake);
    
    // Задаем анимацию: падает вниз и слегка вбок
    requestAnimationFrame(() => {
        snowflake.style.transform = `translate(${(Math.random() - 0.5) * 50}px, 100vh)`;
        snowflake.style.opacity = '0.7';
    });
    
    // Удаляем после окончания анимации
    setTimeout(() => snowflake.remove(), duration * 1000);
}

// Создаем снежинки каждые 100 миллисекунд
setInterval(createSnowflake, 100);